import React, { useState } from 'react';

function Calendar() {
  // State for modals and dropdowns
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showLocationDropdown, setShowLocationDropdown] = useState(false);
  const [selectedView, setSelectedView] = useState('week'); // 'week' or 'day'

  // For drag-and-drop, we simply rely on native events on the shift cards
  const handleDragStart = (e) => {
    e.dataTransfer.setData("text/plain", e.currentTarget.dataset.id);
    e.currentTarget.classList.add("dragging");
  };

  const handleDragEnd = (e) => {
    e.currentTarget.classList.remove("dragging");
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.currentTarget.classList.add("active");
  };

  const handleDragLeave = (e) => {
    e.currentTarget.classList.remove("active");
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove("active");
    const draggable = document.querySelector(".dragging");
    if (draggable) {
      e.currentTarget.appendChild(draggable);
    }
  };

  // Toggle functions for modals and dropdown
  const toggleLocationDropdown = () => setShowLocationDropdown(!showLocationDropdown);
  const openAddModal = () => setShowAddModal(true);
  const closeAddModal = () => setShowAddModal(false);
  const openEditModal = () => setShowEditModal(true);
  const closeEditModal = () => setShowEditModal(false);

  // Time slots for the calendar
  const timeSlots = [
    "7:00 AM", "8:00 AM", "9:00 AM", "10:00 AM", "11:00 AM",
    "12:00 PM", "1:00 PM", "2:00 PM", "3:00 PM", "4:00 PM",
    "5:00 PM", "6:00 PM", "7:00 PM", "8:00 PM"
  ];

  // Day of the week
  const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

  // Sample shift data
  const shifts = [
    { id: "shift1", employee: "John D.", day: 0, start: "8:00 AM", end: "4:00 PM", color: "pastel-blue" },
    { id: "shift2", employee: "Sarah M.", day: 1, start: "9:00 AM", end: "5:00 PM", color: "pastel-green" },
    { id: "shift3", employee: "Mike T.", day: 1, start: "12:00 PM", end: "8:00 PM", color: "pastel-pink" },
    { id: "shift4", employee: "Emma L.", day: 2, start: "10:00 AM", end: "6:00 PM", color: "pastel-purple" },
    { id: "shift5", employee: "Alex K.", day: 3, start: "7:00 AM", end: "3:00 PM", color: "pastel-yellow" },
    { id: "shift6", employee: "John D.", day: 4, start: "8:00 AM", end: "4:00 PM", color: "pastel-blue" },
    { id: "shift7", employee: "Sarah M.", day: 5, start: "9:00 AM", end: "5:00 PM", color: "pastel-green" },
    { id: "shift8", employee: "Mike T.", day: 6, start: "12:00 PM", end: "8:00 PM", color: "pastel-pink" },
    { id: "shift9", employee: "Emma L.", day: 6, start: "10:00 AM", end: "6:00 PM", color: "pastel-purple" }
  ];

  // Helper function to find shift position and height
  const getShiftPosition = (start, end) => {
    const startIndex = timeSlots.indexOf(start);
    const endIndex = timeSlots.indexOf(end);

    if (startIndex === -1 || endIndex === -1) return { top: 0, height: 1 };

    return {
      top: startIndex + 1, // +1 to account for the header row
      height: endIndex - startIndex + 1
    };
  };

  // Render the week view
  const renderWeekView = () => (
    <div className="bg-[#1c1e22] rounded-xl shadow-lg overflow-hidden mb-8">
      <div className="grid grid-cols-8 grid-flow-row auto-rows-auto border-collapse">
        {/* Corner cell */}
        <div className="bg-[#1c1e22] p-2 border-b border-r border-[#2a2d32]"></div>

        {/* Day Headers */}
        {days.map((day, i) => (
          <div key={i} className="bg-[#1c1e22] p-3 text-center font-medium border-b border-r border-[#2a2d32]">{day}</div>
        ))}

        {/* Time slots and grid cells */}
        {timeSlots.map((time, timeIndex) => (
          <React.Fragment key={timeIndex}>
            {/* Time label */}
            <div className="bg-[#1c1e22] p-2 border-b border-r border-[#2a2d32] text-xs text-right pr-2">
              {time}
            </div>

            {/* Grid cells for each day */}
            {days.map((_, dayIndex) => (
              <div 
                key={`${timeIndex}-${dayIndex}`}
                className="bg-[#1c1e22] border-b border-r border-[#2a2d32] relative drop-zone" 
                data-day={dayIndex}
                data-time={time}
                style={{ minHeight: '36px' }}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
              >
                {/* Render shifts that start at this time slot */}
                {shifts
                  .filter(shift => shift.day === dayIndex && shift.start === time)
                  .map(shift => {
                    const { height } = getShiftPosition(shift.start, shift.end);
                    return (
                      <div 
                        key={shift.id}
                        className={`shift-card ${shift.color} p-2 rounded absolute inset-x-1 z-10 cursor-move draggable shadow-md text-left`} 
                        style={{ height: `${height * 36 - 2}px` }}
                        data-id={shift.id} 
                        draggable="true"
                        onDragStart={handleDragStart}
                        onDragEnd={handleDragEnd}
                      >
                        <div className="font-medium text-xs truncate">{shift.employee}</div>
                        <div className="text-xs truncate">{shift.start} - {shift.end}</div>
                      </div>
                    );
                  })}
              </div>
            ))}
          </React.Fragment>
        ))}
      </div>
    </div>
  );

  return (
    <div className="bg-[#1a1d20] text-gray-200 flex-1 flex flex-col overflow-auto h-full">
      {/* Header Section */}
      <header className="bg-[#16181a] py-4 px-6 shadow-lg flex-shrink-0">
        <div className="container mx-auto flex flex-col md:flex-row justify-between items-center">
          <div className="flex items-center mb-4 md:mb-0">
            {/* Removed "Shift Scheduler" text */}
          </div>

          <div className="flex flex-wrap gap-3">
            <div className="relative">
              <button 
                id="locationDropdownBtn" 
                onClick={toggleLocationDropdown}
                className="flex items-center bg-[#1e2024] hover:bg-[#23252a] px-4 py-2 rounded-lg transition"
              >
                <i className="fas fa-store mr-2 text-blue-400"></i>
                <span>Downtown Location</span>
                <i className="fas fa-chevron-down ml-2 text-sm"></i>
              </button>
              {showLocationDropdown && (
                <div id="locationDropdown" className="absolute z-10 mt-2 w-48 bg-[#1e2024] rounded-lg shadow-lg">
                  <a href="#" className="block px-4 py-2 hover:bg-[#23252a] rounded-t-lg">Downtown Location</a>
                  <a href="#" className="block px-4 py-2 hover:bg-[#23252a]">Uptown Location</a>
                  <a href="#" className="block px-4 py-2 hover:bg-[#23252a] rounded-b-lg">Westside Location</a>
                </div>
              )}
            </div>

            <button className="flex items-center bg-[#1e2024] hover:bg-[#23252a] px-4 py-2 rounded-lg transition">
              <i className="fas fa-users mr-2 text-green-400"></i>
              <span>
                Team <span className="bg-blue-500 text-white px-2 py-1 rounded-full text-xs ml-1">12</span>
              </span>
            </button>

            <button 
              id="addEmployeeBtn" 
              onClick={openAddModal}
              className="flex items-center bg-blue-600 hover:bg-blue-500 px-4 py-2 rounded-lg transition"
            >
              <i className="fas fa-plus mr-2"></i>
              <span>Add Employee</span>
            </button>
          </div>
        </div>
      </header>

      {/* Calendar Navigation */}
      <div className="container mx-auto px-6 py-4">
        <div className="flex justify-between items-center mb-6">
          <button className="flex items-center bg-[#1e2024] hover:bg-[#23252a] px-4 py-2 rounded-lg transition">
            <i className="fas fa-chevron-left mr-2"></i>
            Previous Week
          </button>

          <h2 className="text-xl font-semibold">June 11 - June 17, 2023</h2>

          <button className="flex items-center bg-[#1e2024] hover:bg-[#23252a] px-4 py-2 rounded-lg transition">
            Next Week
            <i className="fas fa-chevron-right ml-2"></i>
          </button>
        </div>

        {/* View Selector */}
        <div className="flex mb-4 gap-2">
          <button 
            className={`px-4 py-2 rounded-lg transition ${selectedView === 'week' ? 'bg-blue-600 text-white' : 'bg-[#1e2024] hover:bg-[#23252a]'}`}
            onClick={() => setSelectedView('week')}
          >
            Week View
          </button>
          <button 
            className={`px-4 py-2 rounded-lg transition ${selectedView === 'day' ? 'bg-blue-600 text-white' : 'bg-[#1e2024] hover:bg-[#23252a]'}`}
            onClick={() => setSelectedView('day')}
          >
            Day View
          </button>
        </div>

        {/* Calendar Grid */}
        {renderWeekView()}

        {/* Employee List Section */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4">Team Members</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
            {/* Employee Card 1 */}
            <div className="bg-[#1d1f23] rounded-lg p-4 shadow hover:shadow-lg transition">
              <div className="flex items-center mb-3">
                <div className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center text-white font-medium mr-3">JD</div>
                <div>
                  <div className="font-medium">John Doe</div>
                  <div className="text-sm text-gray-400">Full-time</div>
                </div>
              </div>
              <div className="flex justify-end">
                <button 
                  className="edit-employee-btn bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded-md text-sm transition" 
                  onClick={openEditModal}
                >
                  Edit
                </button>
              </div>
            </div>
            {/* Employee Card 2 */}
            <div className="bg-[#1d1f23] rounded-lg p-4 shadow hover:shadow-lg transition">
              <div className="flex items-center mb-3">
                <div className="w-10 h-10 rounded-full bg-green-500 flex items-center justify-center text-white font-medium mr-3">SM</div>
                <div>
                  <div className="font-medium">Sarah Miller</div>
                  <div className="text-sm text-gray-400">Full-time</div>
                </div>
              </div>
              <div className="flex justify-end">
                <button 
                  className="edit-employee-btn bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded-md text-sm transition" 
                  onClick={openEditModal}
                >
                  Edit
                </button>
              </div>
            </div>
            {/* Employee Card 3 */}
            <div className="bg-[#1d1f23] rounded-lg p-4 shadow hover:shadow-lg transition">
              <div className="flex items-center mb-3">
                <div className="w-10 h-10 rounded-full bg-pink-500 flex items-center justify-center text-white font-medium mr-3">MT</div>
                <div>
                  <div className="font-medium">Mike Taylor</div>
                  <div className="text-sm text-gray-400">Part-time</div>
                </div>
              </div>
              <div className="flex justify-end">
                <button 
                  className="edit-employee-btn bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded-md text-sm transition" 
                  onClick={openEditModal}
                >
                  Edit
                </button>
              </div>
            </div>
            {/* Employee Card 4 */}
            <div className="bg-[#1d1f23] rounded-lg p-4 shadow hover:shadow-lg transition">
              <div className="flex items-center mb-3">
                <div className="w-10 h-10 rounded-full bg-purple-500 flex items-center justify-center text-white font-medium mr-3">EL</div>
                <div>
                  <div className="font-medium">Emma Lee</div>
                  <div className="text-sm text-gray-400">Part-time</div>
                </div>
              </div>
              <div className="flex justify-end">
                <button 
                  className="edit-employee-btn bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded-md text-sm transition" 
                  onClick={openEditModal}
                >
                  Edit
                </button>
              </div>
            </div>
            {/* Employee Card 5 */}
            <div className="bg-[#1d1f23] rounded-lg p-4 shadow hover:shadow-lg transition">
              <div className="flex items-center mb-3">
                <div className="w-10 h-10 rounded-full bg-yellow-500 flex items-center justify-center text-white font-medium mr-3">AK</div>
                <div>
                  <div className="font-medium">Alex Kim</div>
                  <div className="text-sm text-gray-400">Full-time</div>
                </div>
              </div>
              <div className="flex justify-end">
                <button 
                  className="edit-employee-btn bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded-md text-sm transition" 
                  onClick={openEditModal}
                >
                  Edit
                </button>
              </div>
            </div>
            {/* Employee Card 6 */}
            <div className="bg-[#1d1f23] rounded-lg p-4 shadow hover:shadow-lg transition">
              <div className="flex items-center mb-3">
                <div className="w-10 h-10 rounded-full bg-red-500 flex items-center justify-center text-white font-medium mr-3">RJ</div>
                <div>
                  <div className="font-medium">Robert Johnson</div>
                  <div className="text-sm text-gray-400">Part-time</div>
                </div>
              </div>
              <div className="flex justify-end">
                <button 
                  className="edit-employee-btn bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded-md text-sm transition" 
                  onClick={openEditModal}
                >
                  Edit
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Add Employee Modal */}
      {showAddModal && (
        <div id="addEmployeeModal" className="modal fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 transition opacity-100">
          <div className="bg-[#16181a] rounded-lg shadow-xl w-full max-w-md p-6 mx-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold">Add New Employee</h3>
              <button onClick={closeAddModal} className="text-gray-400 hover:text-white">
                <i className="fas fa-times"></i>
              </button>
            </div>
            <form id="addEmployeeForm" onSubmit={(e) => { e.preventDefault(); alert('Employee added successfully!'); closeAddModal(); }}>
              <div className="mb-4">
                <label className="block text-gray-300 mb-2">Full Name</label>
                <input type="text" className="w-full bg-[#1d1f23] border border-gray-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" required />
              </div>
              <div className="mb-4">
                <label className="block text-gray-300 mb-2">Email</label>
                <input type="email" className="w-full bg-[#1d1f23] border border-gray-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" required />
              </div>
              <div className="mb-4">
                <label className="block text-gray-300 mb-2">Position</label>
                <select className="w-full bg-[#1d1f23] border border-gray-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" required>
                  <option value="">Select Position</option>
                  <option value="cashier">Cashier</option>
                  <option value="manager">Manager</option>
                  <option value="barista">Barista</option>
                  <option value="cook">Cook</option>
                  <option value="server">Server</option>
                </select>
              </div>
              <div className="mb-6">
                <label className="block text-gray-300 mb-2">Employment Type</label>
                <div className="flex items-center space-x-4">
                  <label className="inline-flex items-center">
                    <input type="radio" name="employmentType" value="full-time" className="text-blue-500" defaultChecked />
                    <span className="ml-2">Full-time</span>
                  </label>
                  <label className="inline-flex items-center">
                    <input type="radio" name="employmentType" value="part-time" className="text-blue-500" />
                    <span className="ml-2">Part-time</span>
                  </label>
                </div>
              </div>
              <div className="flex justify-end space-x-3">
                <button type="button" onClick={closeAddModal} className="px-4 py-2 bg-[#23252a] hover:bg-[#2b2d33] rounded-lg transition">
                  Cancel
                </button>
                <button type="submit" className="px-4 py-2 bg-blue-600 hover:bg-blue-500 rounded-lg transition">
                  Add Employee
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Edit Employee Modal */}
      {showEditModal && (
        <div id="editEmployeeModal" className="modal fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 transition opacity-100">
          <div className="bg-[#16181a] rounded-lg shadow-xl w-full max-w-2xl p-6 mx-4">
