import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Calendar from './components/Calendar';
import Chatbot from './components/Chatbot';
import AddEmployeeModal from './components/AddEmployeeModal';
import EditEmployeeModal from './components/EditEmployeeModal';

function App() {
  // Hard-coded employee data for demonstration
  const [employees, setEmployees] = useState([
    { id: 1, name: 'John Doe', role: 'Server', availability: 'Mon-Fri' },
    { id: 2, name: 'Jane Smith', role: 'Chef', availability: 'Wed-Sun' },
    { id: 3, name: 'Emily Johnson', role: 'Host', availability: 'Sat-Sun' },
  ]);

  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [isMobile, setIsMobile] = useState(false);
  const [showChatbot, setShowChatbot] = useState(true);

  // Check if screen size is mobile
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };

    // Initial check
    checkMobile();

    // Add event listener
    window.addEventListener('resize', checkMobile);

    // Cleanup
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Modal functions
  const openAddModal = () => setShowAddModal(true);
  const closeAddModal = () => setShowAddModal(false);

  const openEditModal = (employee) => {
    setSelectedEmployee(employee);
    setShowEditModal(true);
  };
  const closeEditModal = () => {
    setSelectedEmployee(null);
    setShowEditModal(false);
  };

  const addEmployee = (employee) => {
    setEmployees([...employees, { ...employee, id: employees.length + 1 }]);
  };

  // Toggle chatbot visibility (for mobile)
  const toggleChatbot = () => {
    setShowChatbot(!showChatbot);
  };

  return (
    <div className="flex flex-col h-screen bg-[#16181a] text-white">
      <Header toggleChatbot={toggleChatbot} isMobile={isMobile} />
      <div className={`flex flex-1 overflow-hidden ${isMobile ? 'flex-col' : ''}`}>
        <div className={`flex ${isMobile ? 'flex-col' : 'flex-1'} overflow-hidden`}>
          {(!isMobile || !showChatbot) && <Calendar employees={employees} onAddEmployee={openAddModal} onEditEmployee={openEditModal} />}
          {(!isMobile || showChatbot) && <Chatbot />}
        </div>
      </div>
      {showAddModal && (
        <AddEmployeeModal onClose={closeAddModal} onAdd={addEmployee} />
      )}
      {showEditModal && selectedEmployee && (
        <EditEmployeeModal employee={selectedEmployee} onClose={closeEditModal} />
      )}
    </div>
  );
}

export default App;
