import React, { useState } from 'react';

function Chatbot() {
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'Hi! How can I help with scheduling today?' }
  ]);
  const [input, setInput] = useState('');

  const sendMessage = () => {
    if (input.trim() === '') return;
    const newMsg = { sender: 'user', text: input };
    setMessages([...messages, newMsg]);
    setInput('');
    // Simulate a bot response for now
    setTimeout(() => {
      setMessages(prev => [...prev, { sender: 'bot', text: 'This is a placeholder response.' }]);
    }, 1000);
  };

  return (
    <aside className="w-64 bg-white p-4 border-l border-gray-200 flex flex-col">
      <h2 className="text-lg font-semibold mb-2">AI Chatbot</h2>
      <div className="bg-gray-50 p-2 h-64 overflow-y-auto mb-2">
        {messages.map((msg, index) => (
          <div key={index} className={`text-sm ${msg.sender === 'bot' ? 'text-gray-500' : 'text-black text-right'}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="flex">
        <input 
          type="text" 
          className="flex-1 border rounded p-2" 
          placeholder="Type a message..." 
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button 
          onClick={sendMessage} 
          className="bg-blue-500 text-white px-4 py-2 rounded ml-2"
        >
          Send
        </button>
      </div>
    </aside>
  );
}

export default Chatbot;
