import React from 'react';

function Calendar({ employees }) {
  const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

  return (
    <main className="flex-1 p-4 overflow-auto bg-[#16181a] h-full">
      <h2 className="text-xl font-semibold mb-4 text-white">Weekly Schedule</h2>
      <table className="w-full border-collapse">
        <thead>
          <tr>
            <th className="border border-gray-700 p-2 bg-gray-800 text-left text-white">Employee</th>
            {days.map(day => (
              <th key={day} className="border border-gray-700 p-2 bg-gray-800 text-white">{day}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map(emp => (
            <tr key={emp.id} className="text-center">
              <td className="border border-gray-700 p-2 text-left text-white">
                <span className="font-medium">{emp.name}</span>
              </td>
              {days.map(day => (
                <td key={day} className="border border-gray-700 p-2 text-gray-300">
                  {/* Hard-coded availability placeholder. This can later be dynamically updated. */}
                  {emp.availability.includes(day.substring(0,3)) ? 'Available' : '—'}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </main>
  );
}

export default Calendar;
