import React, { useState } from 'react';

function Calendar({ employees, onAddEmployee, onEditEmployee }) {
  const [showAddEmployeeModal, setShowAddEmployeeModal] = useState(false);
  const [showEditEmployeeModal, setShowEditEmployeeModal] = useState(false);
  const [showLocationDropdown, setShowLocationDropdown] = useState(false);

  // Drag and drop event handlers (as in your code)
  const handleDragStart = (e) => {
    e.dataTransfer.setData("text/plain", e.currentTarget.dataset.id);
    e.currentTarget.classList.add("dragging");
  };

  const handleDragEnd = (e) => {
    e.currentTarget.classList.remove("dragging");
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.currentTarget.classList.add("active");
  };

  const handleDragLeave = (e) => {
    e.currentTarget.classList.remove("active");
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove("active");
    const draggable = document.querySelector(".dragging");
    if (draggable) {
      e.currentTarget.appendChild(draggable);
    }
  };

  // Toggle functions
  const toggleLocationDropdown = () => setShowLocationDropdown(!showLocationDropdown);
  const openAddEmployeeModal = () => setShowAddEmployeeModal(true);
  const closeAddEmployeeModal = () => setShowAddEmployeeModal(false);
  const openEditEmployeeModal = () => setShowEditEmployeeModal(true);
  const closeEditEmployeeModal = () => setShowEditEmployeeModal(false);

  return (
    <div className="bg-gray-900 text-gray-200 min-h-screen">
      {/* Header Section */}
      <header className="bg-gray-800 py-4 px-6 shadow-lg">
        <div className="container mx-auto flex flex-col md:flex-row justify-between items-center">
          <div className="flex items-center mb-4 md:mb-0">
            <h1 className="text-2xl font-bold text-blue-400">Shift Scheduler</h1>
          </div>
          <div className="flex flex-wrap gap-3">
            <div className="relative">
              <button
                id="locationDropdownBtn"
                onClick={toggleLocationDropdown}
                className="flex items-center bg-gray-700 hover:bg-gray-600 px-4 py-2 rounded-lg transition"
              >
                <i className="fas fa-store mr-2 text-blue-400"></i>
                <span>Downtown Location</span>
                <i className="fas fa-chevron-down ml-2 text-sm"></i>
              </button>
              {showLocationDropdown && (
                <div id="locationDropdown" className="absolute z-10 mt-2 w-48 bg-gray-700 rounded-lg shadow-lg">
                  <a href="#" className="block px-4 py-2 hover:bg-gray-600 rounded-t-lg">Downtown Location</a>
                  <a href="#" className="block px-4 py-2 hover:bg-gray-600">Uptown Location</a>
                  <a href="#" className="block px-4 py-2 hover:bg-gray-600 rounded-b-lg">Westside Location</a>
                </div>
              )}
            </div>
            <button
              id="teamBtn"
              className="flex items-center bg-gray-700 hover:bg-gray-600 px-4 py-2 rounded-lg transition"
            >
              <i className="fas fa-users mr-2 text-green-400"></i>
              <span>Team <span className="bg-blue-500 text-white px-2 py-1 rounded-full text-xs ml-1">12</span></span>
            </button>
            <button
              id="addEmployeeBtn"
              onClick={openAddEmployeeModal}
              className="flex items-center bg-blue-600 hover:bg-blue-500 px-4 py-2 rounded-lg transition"
            >
              <i className="fas fa-plus mr-2"></i>
              <span>Add Employee</span>
            </button>
          </div>
        </div>
      </header>

      {/* Calendar Navigation */}
      <div className="container mx-auto px-6 py-4">
        <div className="flex justify-between items-center mb-6">
          <button className="flex items-center bg-gray-700 hover:bg-gray-600 px-4 py-2 rounded-lg transition">
            <i className="fas fa-chevron-left mr-2"></i>
            Previous Week
          </button>
          <h2 className="text-xl font-semibold">June 11 - June 17, 2023</h2>
          <button className="flex items-center bg-gray-700 hover:bg-gray-600 px-4 py-2 rounded-lg transition">
            Next Week
            <i className="fas fa-chevron-right ml-2"></i>
          </button>
        </div>

        {/* Calendar Grid */}
        <div className="bg-gray-800 rounded-xl shadow-lg overflow-hidden mb-8">
          <div className="grid grid-cols-7 gap-px bg-gray-700">
            {/* Day Headers */}
            {["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"].map((day, index) => (
              <div key={index} className="bg-gray-800 p-3 text-center font-medium">
                {day}
              </div>
            ))}
            {/* Day Cells with shift cards */}
            <div className="bg-gray-800 p-3 drop-zone min-h-40" data-day="sunday"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
            >
              <div className="shift-card pastel-blue p-2 mb-2 rounded-lg cursor-move draggable shadow" 
                data-id="shift1" draggable="true"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">John D.</div>
                <div className="text-xs">8:00 AM - 4:00 PM</div>
              </div>
            </div>
            {/* Repeat for the other days… */}
          </div>
        </div>
      </div>

      {/* Add Employee Modal */}
      {showAddEmployeeModal && (
        <div id="addEmployeeModal" className="modal fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 transition opacity-100">
          <div className="bg-gray-800 rounded-lg shadow-xl w-full max-w-md p-6 mx-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold">Add New Employee</h3>
              <button onClick={closeAddEmployeeModal} className="text-gray-400 hover:text-white">
                <i className="fas fa-times"></i>
              </button>
            </div>
            <form id="addEmployeeForm" onSubmit={(e) => { e.preventDefault(); alert('Employee added successfully!'); closeAddEmployeeModal(); }}>
              {/* Form fields */}
            </form>
          </div>
        </div>
      )}

      {/* Edit Employee Modal */}
      {showEditEmployeeModal && (
        <div id="editEmployeeModal" className="modal fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 transition opacity-100">
          <div className="bg-gray-800 rounded-lg shadow-xl w-full max-w-2xl p-6 mx-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold">Edit Employee Schedule</h3>
              <button onClick={closeEditEmployeeModal} className="text-gray-400 hover:text-white">
                <i className="fas fa-times"></i>
              </button>
            </div>
            {/* Edit modal content */}
          </div>
        </div>
      )}
    </div>
  );
}

export default Calendar;
