import React, { useState, useRef, useEffect } from 'react';

function Calendar({ employees, onAddEmployee, onEditEmployee }) {
  // Only maintain state for internal modals and dropdowns
  const [showTeamModal, setShowTeamModal] = useState(false);
  const [showLocationDropdown, setShowLocationDropdown] = useState(false);

  // Create refs for the modals and dropdowns
  const locationDropdownRef = useRef(null);
  const locationButtonRef = useRef(null);
  const teamModalRef = useRef(null);

  // Drag and drop handlers
  const handleDragStart = (e) => {
    e.dataTransfer.setData("text/plain", e.currentTarget.dataset.id);
    e.currentTarget.classList.add("dragging");
  };

  const handleDragEnd = (e) => {
    e.currentTarget.classList.remove("dragging");
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.currentTarget.classList.add("active");
  };

  const handleDragLeave = (e) => {
    e.currentTarget.classList.remove("active");
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove("active");
    const draggable = document.querySelector(".dragging");
    if (draggable) {
      e.currentTarget.appendChild(draggable);
    }
  };

  // Modal and dropdown toggle functions
  const toggleLocationDropdown = () => setShowLocationDropdown(!showLocationDropdown);
  const openTeamModal = () => setShowTeamModal(true);
  const closeTeamModal = () => setShowTeamModal(false);

  // Handle click events for external modals
  const handleAddEmployee = () => {
    if (showTeamModal) closeTeamModal();
    onAddEmployee();
  };

  const handleEditEmployee = (employee) => {
    if (showTeamModal) closeTeamModal();
    onEditEmployee(employee);
  };

  // Handle click outside for dropdown
  useEffect(() => {
    function handleClickOutside(event) {
      // Handle location dropdown
      if (
        locationDropdownRef.current && 
        !locationDropdownRef.current.contains(event.target) &&
        locationButtonRef.current && 
        !locationButtonRef.current.contains(event.target)
      ) {
        setShowLocationDropdown(false);
      }

      // Handle team modal
      if (
        showTeamModal &&
        teamModalRef.current && 
        !teamModalRef.current.contains(event.target)
      ) {
        closeTeamModal();
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [showTeamModal, showLocationDropdown]);

  return (
    <div className="bg-[#1a1d20] text-gray-200 flex-1 flex flex-col overflow-auto h-full">
      {/* Header Section */}
      <header className="bg-[#16181a] py-4 px-6 shadow-lg flex-shrink-0">
        <div className="container mx-auto flex flex-col md:flex-row justify-between items-center">
          <div className="flex items-center mb-4 md:mb-0">
            <div className="w-10 h-10 bg-gray-700 text-white rounded-full flex items-center justify-center font-bold mr-3">
              R
            </div>
            <h1 className="text-xl font-semibold text-white">Restaurant Name</h1>
          </div>
          <div className="flex flex-wrap gap-3">
            <div className="relative">
              <button 
                ref={locationButtonRef}
                onClick={toggleLocationDropdown}
                className="flex items-center bg-[#1e2024] hover:bg-[#23252a] px-4 py-2 rounded-lg transition"
              >
                <i className="fas fa-store mr-2 text-blue-400"></i>
                <span>Downtown Location</span>
                <i className="fas fa-chevron-down ml-2 text-sm"></i>
              </button>
              {showLocationDropdown && (
                <div 
                  ref={locationDropdownRef}
                  className="absolute z-10 mt-2 w-48 bg-[#1e2024] rounded-lg shadow-lg"
                >
                  <a href="#" className="block px-4 py-2 hover:bg-[#23252a] rounded-t-lg">Downtown Location</a>
                  <a href="#" className="block px-4 py-2 hover:bg-[#23252a]">Uptown Location</a>
                  <a href="#" className="block px-4 py-2 hover:bg-[#23252a] rounded-b-lg">Westside Location</a>
                </div>
              )}
            </div>
            <button 
              onClick={openTeamModal}
              className="flex items-center bg-[#1e2024] hover:bg-[#23252a] px-4 py-2 rounded-lg transition"
            >
              <i className="fas fa-users mr-2 text-green-400"></i>
              <span>
                Team <span className="bg-blue-500 text-white px-2 py-1 rounded-full text-xs ml-1">{employees.length}</span>
              </span>
            </button>
            <button 
              onClick={handleAddEmployee}
              className="flex items-center bg-blue-600 hover:bg-blue-500 px-4 py-2 rounded-lg transition"
            >
              <i className="fas fa-plus mr-2"></i>
              <span>Add Employee</span>
            </button>
          </div>
        </div>
      </header>

      {/* Calendar Navigation */}
      <div className="container mx-auto px-6 py-4">
        <div className="flex justify-between items-center mb-6">
          <button className="flex items-center bg-[#1e2024] hover:bg-[#23252a] px-4 py-2 rounded-lg transition">
            <i className="fas fa-chevron-left mr-2"></i>
            Previous Week
          </button>
          <h2 className="text-xl font-semibold">June 11 - June 17, 2023</h2>
          <button className="flex items-center bg-[#1e2024] hover:bg-[#23252a] px-4 py-2 rounded-lg transition">
            Next Week
            <i className="fas fa-chevron-right ml-2"></i>
          </button>
        </div>

        {/* Calendar Grid */}
        <div className="bg-[#1c1e22] rounded-xl shadow-lg overflow-hidden mb-8">
          <div className="grid grid-cols-7 border border-[#2a2d32] divide-x divide-[#2a2d32]">
            {/* Day Headers */}
            {["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"].map((day, index) => (
              <div key={index} className="bg-[#1c1e22] p-3 text-center font-medium border-b border-[#2a2d32]">{day}</div>
            ))}
            {/* Day Cells with hard-coded shift cards */}
            <div className="bg-[#1c1e22] p-3 drop-zone min-h-40" data-day="sunday"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
            >
              <div className="shift-card pastel-blue p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift1"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">John D.</div>
                <div className="text-xs">8:00 AM - 4:00 PM</div>
              </div>
            </div>
            <div className="bg-[#1c1e22] p-3 drop-zone min-h-40" data-day="monday"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
            >
              <div className="shift-card pastel-green p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift2"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">Sarah M.</div>
                <div className="text-xs">9:00 AM - 5:00 PM</div>
              </div>
              <div className="shift-card pastel-pink p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift3"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">Mike T.</div>
                <div className="text-xs">12:00 PM - 8:00 PM</div>
              </div>
            </div>
            <div className="bg-[#1c1e22] p-3 drop-zone min-h-40" data-day="tuesday"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
            >
              <div className="shift-card pastel-purple p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift4"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">Emma L.</div>
                <div className="text-xs">10:00 AM - 6:00 PM</div>
              </div>
            </div>
            <div className="bg-[#1c1e22] p-3 drop-zone min-h-40" data-day="wednesday"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
            >
              <div className="shift-card pastel-yellow p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift5"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">Alex K.</div>
                <div className="text-xs">7:00 AM - 3:00 PM</div>
              </div>
            </div>
            <div className="bg-[#1c1e22] p-3 drop-zone min-h-40" data-day="thursday"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
            >
              <div className="shift-card pastel-blue p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift6"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">John D.</div>
                <div className="text-xs">8:00 AM - 4:00 PM</div>
              </div>
            </div>
            <div className="bg-[#1c1e22] p-3 drop-zone min-h-40" data-day="friday"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
            >
              <div className="shift-card pastel-green p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift7"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">Sarah M.</div>
                <div className="text-xs">9:00 AM - 5:00 PM</div>
              </div>
            </div>
            <div className="bg-[#1c1e22] p-3 drop-zone min-h-40" data-day="saturday"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
            >
              <div className="shift-card pastel-pink p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift8"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">Mike T.</div>
                <div className="text-xs">12:00 PM - 8:00 PM</div>
              </div>
              <div className="shift-card pastel-purple p-2 mb-2 rounded-lg cursor-move draggable shadow" draggable="true"
                data-id="shift9"
                onDragStart={handleDragStart}
                onDragEnd={handleDragEnd}
              >
                <div className="font-medium">Emma L.</div>
                <div className="text-xs">10:00 AM - 6:00 PM</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Employee List Section */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Team Members</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
          {employees.map(employee => (
            <div key={employee.id} className="bg-[#1d1f23] rounded-lg p-4 shadow hover:shadow-lg transition">
              <div className="flex items-center mb-3">
                <div className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center text-white font-medium mr-3">
                  {employee.name.split(' ').map(n => n[0]).join('')}
                </div>
                <div>
                  <div className="font-medium">{employee.name}</div>
                  <div className="text-sm text-gray-400">{employee.role}</div>
                </div>
              </div>
              <div className="flex justify-end">
                <button 
                  className="edit-employee-btn bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded-md text-sm transition" 
                  onClick={() => handleEditEmployee(employee)}
                >
                  Edit
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Team Members Modal */}
      {showTeamModal && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 transition opacity-100"
          onClick={closeTeamModal}
        >
          <div 
            ref={teamModalRef}
            className="bg-[#16181a] rounded-lg shadow-xl w-full max-w-4xl p-6 mx-4 max-h-[90vh] overflow-y-auto"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-2xl font-bold">Team Members ({employees.length})</h3>
              <button onClick={closeTeamModal} className="text-gray-400 hover:text-white">
                <i className="fas fa-times"></i>
              </button>
            </div>
            {/* Team member cards */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
              {employees.map(employee => (
                <div key={employee.id} className="team-member-card bg-[#1d1f23] rounded-lg p-4 shadow hover:shadow-lg transition cursor-pointer">
                  <div className="flex items-center mb-3">
                    <div className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center text-white font-medium mr-3">
                      {employee.name.split(' ').map(n => n[0]).join('')}
                    </div>
                    <div className="flex-1">
                      <div className="font-medium">{employee.name}</div>
                      <div className="text-sm text-gray-400">{employee.role}</div>
                    </div>
                    <button 
                      onClick={() => handleEditEmployee(employee)} 
                      className="edit-employee-btn bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded-md text-sm transition"
                    >
                      Edit
                    </button>
                  </div>
                  <div className="flex justify-between text-xs">
                    <span className="bg-blue-900 bg-opacity-50 px-2 py-1 rounded">{employee.availability}</span>
                  </div>
                </div>
              ))}
            </div>
            <div className="flex justify-end">
              <button onClick={handleAddEmployee} className="flex items-center bg-blue-600 hover:bg-blue-500 px-4 py-2 rounded-lg transition">
                <i className="fas fa-plus mr-2"></i>
                <span>Add Employee</span>
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Calendar;
