import React from 'react';

function Sidebar({ employees, onAddEmployee, onEditEmployee }) {
  return (
    <aside className="w-64 bg-white p-4 border-r border-gray-200">
      <div className="mb-4">
        <button 
          onClick={onAddEmployee}
          className="bg-green-500 text-white px-4 py-2 rounded-md w-full mb-2"
        >
          Add Employee
        </button>
      </div>
      <div>
        <h2 className="text-lg font-semibold mb-2">Restaurant Team</h2>
        <ul className="space-y-2">
          {employees.map(emp => (
            <li 
              key={emp.id} 
              className="p-2 bg-gray-50 hover:bg-gray-100 rounded-md cursor-pointer"
              onClick={() => onEditEmployee(emp)}
            >
              <div className="font-medium">{emp.name}</div>
              <div className="text-sm text-gray-500">{emp.role}</div>
            </li>
          ))}
        </ul>
      </div>
    </aside>
  );
}

export default Sidebar;
