import React, { useState } from 'react';

function AddEmployeeModal({ onClose, onAdd }) {
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [availability, setAvailability] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd({ name, role, availability });
    onClose();
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-70 z-50">
      <div className="bg-[#16181a] p-6 rounded shadow-lg w-full max-w-md border border-gray-700">
        <h3 className="text-xl font-semibold mb-4 text-white">Add Employee</h3>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block font-medium mb-1 text-gray-300">Name</label>
            <input 
              type="text" 
              className="w-full bg-gray-800 border border-gray-700 rounded p-2 text-white outline-none focus:border-blue-500" 
              value={name}
              onChange={(e) => setName(e.target.value)}
              required 
            />
          </div>
          <div className="mb-4">
            <label className="block font-medium mb-1 text-gray-300">Role</label>
            <input 
              type="text" 
              className="w-full bg-gray-800 border border-gray-700 rounded p-2 text-white outline-none focus:border-blue-500" 
              value={role}
              onChange={(e) => setRole(e.target.value)}
              required 
            />
          </div>
          <div className="mb-4">
            <label className="block font-medium mb-1 text-gray-300">Availability (e.g., "Mon-Fri")</label>
            <input 
              type="text" 
              className="w-full bg-gray-800 border border-gray-700 rounded p-2 text-white outline-none focus:border-blue-500" 
              value={availability}
              onChange={(e) => setAvailability(e.target.value)}
              required 
            />
          </div>
          <div className="flex justify-end space-x-2">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 bg-gray-700 text-white rounded hover:bg-gray-600"
            >
              Cancel
            </button>
            <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-500">
              Add
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AddEmployeeModal;
