import React, { useState } from 'react';

function Chatbot() {
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'Hi! How can I help with scheduling today?' }
  ]);
  const [input, setInput] = useState('');

  const sendMessage = () => {
    if (input.trim() === '') return;
    const newMsg = { sender: 'user', text: input };
    setMessages([...messages, newMsg]);
    setInput('');
    // Simulate a bot response for now
    setTimeout(() => {
      setMessages(prev => [...prev, { sender: 'bot', text: 'This is a placeholder response.' }]);
    }, 1000);
  };

  return (
    <aside className="w-64 bg-[#16181a] p-4 border-l border-gray-700 flex flex-col h-full">
      <h2 className="text-lg font-semibold mb-2 text-white">AI Chatbot</h2>

      {/* Flexible space that pushes the chat to the bottom */}
      <div className="flex-grow"></div>

      <div className="bg-[#16181a] overflow-y-auto mb-2 max-h-64">
        {messages.map((msg, index) => (
          <div key={index} className={`text-sm mb-1 ${msg.sender === 'bot' ? 'text-gray-300' : 'text-white text-right'}`}>
            {msg.text}
          </div>
        ))}
      </div>

      <div className="flex w-full">
        <input 
          type="text" 
          className="flex-1 border border-gray-700 rounded-l p-2 text-sm bg-gray-800 text-white" 
          placeholder="Type a message..." 
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button 
          onClick={sendMessage} 
          className="bg-blue-600 text-white px-3 py-2 rounded-r text-sm"
        >
          Send
        </button>
      </div>
    </aside>
  );
}

export default Chatbot;
